var jsonl = msg.payload.toString().split('\n');

// Check if the data was retrieved successfully
if( jsonl[1].includes("Error") ){
    // The XML error string would be in jsonl[1]
    // <?xml version="1.0" encoding="UTF-8"?>
    // <Error><Code>NoSuchKey</Code><Message>The specified key does not exist.</Message>
    // <Key>records/country_code=mx/device_id=nnn/year=y/month=m/day=d/hour=h/m.jsonl</Key>
    // Report the <Key>
    msg.payload = jsonl[1];
    return [null,null,null,msg];
}
var first_event = JSON.parse(jsonl[0]);

var first_ts = first_event.device_t;
var Xchart = [{
    "series":["X"],
    "data":[[{"x":(first_event.device_t*1000),"y":first_event.x[0]}]],
    "labels":[""]
}];
var Ychart = [{
    "series":["Y"],
    "data":[[{"x":(first_event.device_t*1000),"y":first_event.y[0]}]],
    "labels":[""]
}];
var Zchart = [{
    "series":["Z"],
    "data":[[{"x":(first_event.device_t*1000),"y":first_event.z[0]}]],
    "labels":[""]
}];

var event= {};
var data = {};
for(let i=0; i<jsonl.length-1;i++ ){
    // Build an array of events
    event = JSON.parse(jsonl[i]);

    for(let j=0;j<event.x.length;j++) {

        // Build a X chart
        data = {"x":(event.device_t*1000+(j*3)), "y":event.x[j]} ;
        Xchart[0].data[0].push(data);

        // Build a Y chart
        data = {"x":(event.device_t*1000+(j*3)), "y":event.y[j]} ;
        Ychart[0].data[0].push(data);

        // Build a Z chart
        data = {"x":(event.device_t*1000+(j*3)), "y":event.z[j]} ;
        Zchart[0].data[0].push(data);
    }
}

var xmsg = {};
var ymsg = {};
var zmsg = {};
xmsg.payload = Xchart;
ymsg.payload = Ychart;
zmsg.payload = Zchart;
return [ xmsg, ymsg, zmsg, null];

