// Build a filename for the next 5 minute increment of data
// Sometimes the earthquake tremors span across a 5 minute chunk
// The researcher might want to plot the next block of data

// Be careful of edge cases:
// Adding 5 minutes might increment into the next hour, the next day, the next month, the next year
// It might be a leap year. Leap century
// Let the Date() functions figure that out.

// Query the date, hour, minute selected in the dashboard
var ts     = flow.get("datestamp")||"0";
var hour   = flow.get("hour")||"0";
var minute = flow.get("minute")||"00.jsonl";
minute = minute.slice(0, -6) ; // strip off .jsonl

// Get the epoch time selected in the date picker
// so we can add 5 minutes to it.
var record = new Date(ts);
// Set epoch time to midnight of that date
record.setHours(0,0,0,0);

var msg1 = {};
msg1.payload = "Start - Hour:"+hour+" Minute:"+minute+" Time:"+record.toUTCString();
node.send([null,msg1]);

// Get the hour and minute selected in the dashboard
hour   = parseInt(hour,10);   // strip off "0n", base 10
minute = parseInt(minute,10); // strip off "0n", base 10

// Build a new epoch timestamp and add 5 minutes to it.
// We know the GMT date at midnight
// We know the hour * 3600000 milliseconds
// We know the minute * 60000 milliseconds
// Add 5 minutes = 300000 milliseconds
var newtime = record.getTime() + (hour*3600000) + (minute*60000) + 300000;
record = new Date(newtime);

// Report the new timestamp
msg1.payload = "Next - Hour:"+hour+" Minute:"+minute+" Time:"+record.toUTCString();
node.send([null,msg1]);

var year;
var month;
var day;
year   = record.getFullYear();
month  = ('0' + (record.getMonth()+1)).slice(-2); // padded
day    = ('0' + record.getDate()).slice(-2);      // padded
hour   = ('0' + record.getHours()).slice(-2);     // padded
minute = ('0' + record.getMinutes()).slice(-2);   // padded

// Build a new filename:
// records/country_code=mx/device_id=006/year=2018/month=02/day=16/hour=23/35.jsonl
var device = flow.get("SelectedSensor")||"0";
if( device == '3ef3d787af85' || device == 'db0f06aa9270' ) {
    msg.payload = "records/country_code=pr/device_id="+device+"/year="+year+"/month="+month+"/day="+day+"/hour="+hour+"/"+minute+".jsonl"
} else {
    msg.payload = "records/country_code=mx/device_id="+device+"/year="+year+"/month="+month+"/day="+day+"/hour="+hour+"/"+minute+".jsonl"
}

// Save the times, in case the user presses Next again.
flow.set("datestamp",record.getTime())
flow.set("hour",hour);
flow.set("minute",minute+".jsonl")
msg.url = "https://s3.amazonaws.com/grillo-openeew/"+msg.payload;
return [msg,null];

var duration = flow.get("duration");
var frame = flow.get("frame")||0;
frame+=5;
if( frame < duration ) {
    flow.set("frame",frame);
    return msg;
} else {
    return null;
}
